package ass_303_1;

public class CoreJavaBasics {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//declare and assign two integer values and add them together
		int intVal1= 5,  intVal2 = 10;
		int intSum = intVal1 + intVal2;
		//print the sum
		System.out.println("Integer: " + intVal1 + " + " + intVal2 + " = " + intSum);
		
		//declare and assign two doubles values and add them together
		double doubVal1= 5.7,  doubVal2 = 10.88;
		double doubSum = doubVal1 + doubVal2;
		//print the sum
		System.out.println("Double: " + doubVal1 + " + " + doubVal2 + " = " + doubSum);
	}

}
